import React, { useState, useEffect } from 'react';

const Login = ({ credentials }) => {
  const [state, setState] = useState({
    'email': '',
    'password': ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    credentials(state);
  };

  const handleInputChange = (e) => {
    const target = e.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    setState(state => ({
      ...state,
      [name]: value
    }));
  };

  return (
    <div className='row mt-4  d-flex justify-content-center'>
      <form className='col-4'>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Email address</label>
          <input type="email" className="form-control" name='email' aria-describedby="emailHelp" placeholder="Enter email" onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Password</label>
          <input type="password" className="form-control" name='password' placeholder="Password" onChange={handleInputChange} />
        </div>
        {/* <div className="form-check">
          <input type="checkbox" className="form-check-input" id="exampleCheck1" />
          <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
        </div> */}
        <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Submit</button>
      </form>
    </div>
  );
};

export default Login;
